import React, { useEffect, useState } from "react";
import { fetchHarvests, fetchStats } from "./api";
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  Legend,
  PieChart,
  Pie,
  Cell
} from "recharts";
import "./Dashboard.css";

const COLORS = ["#60a5fa", "#facc15", "#f87171", "#34d399", "#f472b6"];

export default function Dashboard() {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");

  async function loadAll() {
    setLoading(true);
    try {
      const sRes = await fetchStats({ from, to });
      setStats(sRes);
    } catch (e) {
      console.error(e);
      setStats(null);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    loadAll();
  }, []);

  const barData = (stats?.byCrop || []).map(c => ({
    name: c.cropName,
    total: c.sumYield,
    avg: c.avgYield
  }));

  const pieData = (stats?.byCrop || []).map((c, idx) => ({
    name: c.cropName,
    value: c.sumYield,
    color: COLORS[idx % COLORS.length]
  }));

  return (
    <div className="dashboard-root">
      <h1>ðŸŒ¾ CropManager â€” Harvest Dashboard</h1>

      <div className="toolbar">
        <div className="filters">
          <label>
            From
            <input type="date" value={from} onChange={e => setFrom(e.target.value)} />
          </label>
          <label>
            To
            <input type="date" value={to} onChange={e => setTo(e.target.value)} />
          </label>
          <button className="btn" onClick={loadAll} disabled={loading}>Refresh</button>
          <button className="btn ghost" onClick={() => { setFrom(""); setTo(""); loadAll(); }}>Reset</button>
        </div>

        <div className="kpis">
          <div className="kpi card-blue">
            <div className="kpi-label">Total Yield</div>
            <div className="kpi-value">{stats ? stats.totalYield.toLocaleString() : "-" } kg</div>
          </div>
          <div className="kpi card-green">
            <div className="kpi-label">Harvests</div>
            <div className="kpi-value">{stats ? stats.totalHarvests : "-"}</div>
          </div>
        </div>
      </div>

      <div className="visuals">
        <div className="chart-card">
          <h3>Yield by Crop (Bar)</h3>
          <div style={{ height: 350 }}>
            {loading ? <div className="loading">Loadingâ€¦</div> : 
              barData.length === 0 ? <div className="empty">No data</div> :
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={barData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip formatter={v => `${v} kg`} />
                  <Legend />
                  <Bar dataKey="total" fill="#60a5fa" name="Total Yield" />
                  <Bar dataKey="avg" fill="#facc15" name="Avg Yield" />
                </BarChart>
              </ResponsiveContainer>
            }
          </div>
        </div>

        <div className="chart-card">
          <h3>Yield Distribution (Pie)</h3>
          <div style={{ height: 350 }}>
            {loading ? <div className="loading">Loadingâ€¦</div> : 
              pieData.length === 0 ? <div className="empty">No data</div> :
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie 
                    data={pieData} 
                    dataKey="value" 
                    nameKey="name" 
                    outerRadius={120} 
                    label
                  >
                    {pieData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.color} />)}
                  </Pie>
                  <Tooltip formatter={v => `${v} kg`} />
                </PieChart>
              </ResponsiveContainer>
            }
          </div>
        </div>

        <div className="table-card">
          <h3>Crop Breakdown</h3>
          <table className="crop-table">
            <thead>
              <tr>
                <th>Crop</th>
                <th>Harvests</th>
                <th>Total (kg)</th>
                <th>Avg (kg)</th>
              </tr>
            </thead>
            <tbody>
              {(stats?.byCrop || []).map(c => (
                <tr key={c.cropId}>
                  <td>{c.cropName}</td>
                  <td>{c.count}</td>
                  <td>{Math.round(c.sumYield).toLocaleString()}</td>
                  <td>{Math.round(c.avgYield).toLocaleString()}</td>
                </tr>
              ))}
              {(!stats?.byCrop || stats.byCrop.length === 0) && (
                <tr><td colSpan="4" style={{textAlign:"center"}}>No data</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
