import React, { useEffect, useState } from "react";
import { fetchHarvests, fetchStats } from "./api";
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  Legend
} from "recharts";
import "./Dashboard.css";

export default function Dashboard() {
  const [harvests, setHarvests] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");

  async function loadAll() {
    setLoading(true);
    try {
      const [hRes, sRes] = await Promise.all([
        fetchHarvests({ from, to }),
        fetchStats({ from, to })
      ]);
      setHarvests(hRes.harvests || []);
      setStats(sRes);
    } catch (e) {
      console.error(e);
      setHarvests([]);
      setStats(null);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    loadAll();
  }, []);

  const chartData = (stats?.byCrop || []).map(c => ({
    name: c.cropName,
    total: c.sumYield,
    avg: c.avgYield
  }));

  return (
    <div className="dashboard-root">
      <h2>Harvest Dashboard</h2>

      <div className="toolbar">
        <div className="filters">
          <label>
            From
            <input type="date" value={from} onChange={e => setFrom(e.target.value)} />
          </label>
          <label>
            To
            <input type="date" value={to} onChange={e => setTo(e.target.value)} />
          </label>
          <button className="btn" onClick={loadAll} disabled={loading}>
            Refresh
          </button>
          <button className="btn ghost" onClick={() => { setFrom(""); setTo(""); loadAll(); }}>
            Reset
          </button>
        </div>
        <div className="kpis">
          <div className="kpi">
            <div className="kpi-label">Total Yield</div>
            <div className="kpi-value">{stats ? stats.totalYield.toLocaleString() : "-" } kg</div>
          </div>
          <div className="kpi">
            <div className="kpi-label">Harvests</div>
            <div className="kpi-value">{stats ? stats.totalHarvests : "-"}</div>
          </div>
        </div>
      </div>

      <div className="visuals">
        <div className="chart-card">
          <h3>Yield by Crop</h3>
          <div style={{ height: 350 }}>
            {loading ? (
              <div className="loading">Loadingâ€¦</div>
            ) : chartData.length === 0 ? (
              <div className="empty">No data yet</div>
            ) : (
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={chartData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip formatter={v => `${v} kg`} />
                  <Legend />
                  <Bar dataKey="total" fill="#60a5fa" name="Total Yield" />
                  <Bar dataKey="avg" fill="#facc15" name="Avg Yield" />
                </BarChart>
              </ResponsiveContainer>
            )}
          </div>
        </div>

        <div className="table-card">
          <h3>Crop Breakdown</h3>
          <table className="crop-table">
            <thead>
              <tr>
                <th>Crop</th>
                <th>Harvests</th>
                <th>Total (kg)</th>
                <th>Avg (kg)</th>
              </tr>
            </thead>
            <tbody>
              {(stats?.byCrop || []).map(c => (
                <tr key={c.cropId}>
                  <td>{c.cropName}</td>
                  <td>{c.count}</td>
                  <td>{Math.round(c.sumYield).toLocaleString()}</td>
                  <td>{Math.round(c.avgYield).toLocaleString()}</td>
                </tr>
              ))}
              {(!stats?.byCrop || stats.byCrop.length === 0) && (
                <tr>
                  <td colSpan="4" style={{ textAlign: "center", padding: "18px 0" }}>No data</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      <div style={{ marginTop: 18 }}>
        <small>Data source: Student 1's shared DB. Refresh after new harvests.</small>
      </div>
    </div>
  );
}
